SELECT DECODE(SUBSTR(HD.ANN_NO,1,2),'BD',HD.BID_NO,HD.ANN_NO) AS ANN_NO ,HD.ANN_ITEM ,GETICOMCODE2(HD.HOUSE_CODE, 'M974', HD.CONT_TYPE1 ) ||' / '|| GETICOMCODE2(HD.HOUSE_CODE, 'M973', HD.CONT_TYPE2 ) ||' / '|| GETICOMCODE2(HD.HOUSE_CODE, 'M930', HD.PROM_CRIT ) AS CONT_TYPE ,TO_CHAR(TO_DATE(HD.APP_BEGIN_DATE||HD.APP_BEGIN_TIME, 'YYYYMMDDHH24MISS'),'YYYYMMDDHH24MISS') AS CHK_DATE ,TO_CHAR(TO_DATE(HD.APP_BEGIN_DATE||HD.APP_BEGIN_TIME, 'YYYYMMDDHH24MISS'),'YYYY/MM/DD HH24:MI') ||' ~ '|| TO_CHAR(TO_DATE(PG.BID_END_DATE||PG.BID_END_TIME, 'YYYYMMDDHH24MISS'),'YYYY/MM/DD HH24:MI') AS BID_DATE_TIME ,HD.CHANGE_USER_NAME_LOC ,CASE WHEN NVL((SELECT NVL(REQ_YN, 'N') FROM ICOYBDES WHERE HOUSE_CODE = ${HOUSE_CODE} AND HOUSE_CODE = HD.HOUSE_CODE AND BID_NO = HD.BID_NO AND BID_COUNT = HD.BID_COUNT),'N') = 'N' THEN 'N' ELSE (CASE HD.COST_STATUS WHEN 'EC' THEN 'C' ELSE 'R' END) END AS STATUS ,GETICOMCODE2(HD.HOUSE_CODE, 'M975' , CASE WHEN NVL((SELECT NVL(REQ_YN, 'N') FROM ICOYBDES WHERE HOUSE_CODE = ${HOUSE_CODE} AND HOUSE_CODE = HD.HOUSE_CODE AND BID_NO = HD.BID_NO AND BID_COUNT = HD.BID_COUNT),'N') = 'N' THEN 'N' ELSE (CASE HD.COST_STATUS WHEN 'EC' THEN 'C' ELSE 'R' END) END ) AS STATUS_TEXT ,HD.BID_NO ,HD.BID_COUNT ,PG.VOTE_COUNT ,HD.CHANGE_USER_ID ,HD.ESTM_FLAG ,HD.COST_STATUS ,HD.BID_STATUS ,HD.CTRL_CODE -- ,(SELECT ESTM_USER_ID -- FROM ICOMBACO -- WHERE HOUSE_CODE = HD.HOUSE_CODE -- AND COMPANY_CODE = HD.COMPANY_CODE -- AND CTRL_CODE = HD.CTRL_CODE -- AND CTRL_TYPE = 'P') AS ESTM_USER_ID ,'' as ESTM_USER_ID ,HD.BID_TYPE ,GETICOMCODE2(HD.HOUSE_CODE, 'M968', HD.BID_TYPE ) AS BID_TYPE_TEXT ,CASE WHEN HD.ANN_VERSION IS NULL THEN (SELECT CODE FROM SCODE WHERE TYPE = 'VR001') ELSE HD.ANN_VERSION END AS ANN_VERSION ,HD.BID_TYPE FROM ICOYBDHD HD, ICOYBDPG PG WHERE PG.HOUSE_CODE = HD.HOUSE_CODE AND HD.HOUSE_CODE = ${HOUSE_CODE} AND PG.BID_NO = HD.BID_NO AND PG.BID_COUNT = HD.BID_COUNT AND (HD.APP_BEGIN_DATE BETWEEN ${START_CHANGE_DATE} AND ${END_CHANGE_DATE} OR (PG.BID_END_DATE BETWEEN ${START_CHANGE_DATE} AND ${END_CHANGE_DATE} OR PG.BID_END_DATE IS NULL ) ) AND HD.ANN_NO = $S{ANN_NO} AND HD.ANN_ITEM LIKE '%'|| $S{ANN_ITEM} ||'%'
AND CASE WHEN NVL((SELECT NVL(REQ_YN, 'N') FROM ICOYBDES WHERE HOUSE_CODE = ${HOUSE_CODE} AND HOUSE_CODE = HD.HOUSE_CODE AND BID_NO = HD.BID_NO AND BID_COUNT = HD.BID_COUNT),'N') = 'N' THEN 'N' ELSE (CASE HD.COST_STATUS WHEN 'EC' THEN 'C' ELSE 'R' END) END = $S{BID_FLAG}
AND HD.SIGN_STATUS = 'C' AND HD.BID_STATUS IN ('AC', 'UC', 'RC', 'SR', 'SC', 'SB', 'NB') AND HD.SIGN_STATUS IN ('C') AND HD.STATUS IN ('C' ,'R') AND PG.STATUS IN ('C' ,'R') AND HD.ESTM_KIND IN ('U','M') AND HD.BID_TYPE = $S{BID_TYPE} AND HD.CHANGE_USER_NAME_LOC LIKE '%'|| $S{CHANGE_USER_NAME} ||'%' AND HD.BID_TYPE = $S{BID_TYPE} AND HD.ADD_USER_DEPT = ${session.DEPARTMENT} ORDER BY HD.ANN_DATE DESC, HD.ADD_DATE DESC, HD.ADD_TIME DESC, HD.ANN_NO DESC


SELECT
       COUNT(*)
  FROM ICOYBDES
WHERE HOUSE_CODE = ${HOUSE_CODE}
  AND BID_NO     = ${BID_NO}
  AND BID_COUNT  = ${BID_COUNT}  
  AND STATUS IN ('C', 'R')


INSERT INTO ICOYBDES (
                     HOUSE_CODE
                    ,BID_NO
                    ,BID_COUNT
                    ,STATUS
                    ,ADD_DATE
                    ,ADD_TIME
                    ,ADD_USER_ID
                    ,ADD_USER_NAME_LOC
                    ,ADD_USER_NAME_ENG
                    ,ADD_USER_DEPT
                    ,CHANGE_DATE
                    ,CHANGE_TIME
                    ,CHANGE_USER_ID
                    ,CHANGE_USER_NAME_LOC
                    ,CHANGE_USER_NAME_ENG
                    ,CHANGE_USER_DEPT
                    ,BASIC_AMT
                    ,REQ_COMMENT
                    ,ATTACH_NO
                    ,ESTM_USER_ID
                    ,REQ_YN
) VALUES (
                   ${HOUSE_CODE}
                    ,${BID_NO    }
                    ,${BID_COUNT }
                    ,'C'
                    ,TO_CHAR(SYSDATE,'YYYYMMDD')
                    ,TO_CHAR(SYSDATE,'HH24MISS')
                    ,${session.ID}
                    ,${NAME_LOC}
                    ,${NAME_ENG}
                    ,${DEPT    }
                    ,TO_CHAR(SYSDATE,'YYYYMMDD')
                    ,TO_CHAR(SYSDATE,'HH24MISS')
                    ,${session.ID}
                    ,${NAME_LOC}
                    ,${NAME_ENG}
                    ,${DEPT    }
                    ,$S{BASIC_AMT   }	
                    ,$S{REQ_COMMENT }
                    ,$S{attach_no   }
                    ,$S{ESTM_USER_ID}
                    , CASE WHEN ${FLAG}='C' THEN 'Y' ELSE 'N' END 
                    )


		 DELETE FROM ICOYBDEB
         WHERE HOUSE_CODE = ${HOUSE_CODE}
           AND BID_NO     = ${BID_NO}
           AND BID_COUNT  = ${BID_COUNT}


		SELECT NVL(A.COST_STATUS,'NULL') A_COST_STATUS,NVL(A.STATUS,'NULL') A_STATUS,
                     NVL(B.REQ_YN,'NULL') B_REQ_YN,NVL(B.STATUS,'NULL') B_STATUS 
		FROM ICOYBDHD A,ICOYBDES B
		WHERE A.HOUSE_CODE = B.HOUSE_CODE(+)
		AND A.BID_NO = B.BID_NO(+)
		AND A.BID_COUNT = B.BID_COUNT(+)
		AND A.HOUSE_CODE = ${HOUSE_CODE}
		AND A.BID_NO = ${BID_NO}
		AND A.BID_COUNT = ${BID_COUNT}


SELECT TO_NUMBER(APP_BEGIN_DATE||APP_BEGIN_TIME)-TO_NUMBER(TO_CHAR(SYSDATE,'YYYYMMDDHH24MISS')) 
FROM ICOYBDHD 
WHERE HOUSE_CODE = ${HOUSE_CODE}
AND     BID_NO = ${BID_NO}
AND     BID_COUNT = ${BID_COUNT}


UPDATE ICOYBDES
SET  STATUS = 'C', REQ_YN = 'N' ,ESTM_CONFIRM_DATE = NULL , ESTM_CONFIRM_TIME = NULL,
		ESTM_PRICE1 = NULL,ESTM_PRICE2 = NULL,ESTM_PRICE3 = NULL,ESTM_PRICE4 = NULL,
		ESTM_PRICE5 = NULL,ESTM_PRICE6 = NULL,ESTM_PRICE7 = NULL,ESTM_PRICE8 = NULL,ESTM_PRICE9 = NULL,
		ESTM_PRICE10 = NULL,CHOIC_ESTM_PRICE1 = NULL,CHOIC_ESTM_PRICE2 = NULL,CHOIC_ESTM_PRICE3 = NULL,
		CHOIC_ESTM_PRICE4 = NULL,CHOIC_ESTM_PRICE5 = NULL,FINAL_ESTM_PRICE = NULL,
		INPUT_ESTM_PRICE = NULL,ESTM_H_PRICE = NULL,ESTM_L_PRICE = NULL,ESTM_C_PRICE = NULL
WHERE HOUSE_CODE = ${HOUSE_CODE}
AND     BID_NO = ${BID_NO}
AND     BID_COUNT = ${BID_COUNT}


UPDATE ICOYBDHD
SET COST_STATUS = NULL
WHERE HOUSE_CODE = ${HOUSE_CODE}
AND BID_NO = ${BID_NO}
AND BID_COUNT = ${BID_COUNT}
