	SELECT TO_CHAR(TO_DATE(CHK_DATE,'YYYYMMDD'),'YYYY. MM. DD') AS  CHK_DATE 
		  ,CHK_TIME
		  ,CHK_USER_ID
		  ,GETUSERNAMELOC(${session.HOUSE_CODE}, CHK_USER_ID) AS CHK_USER_NAME
	FROM SCPGL
	WHERE COMPANY_CODE = ${COMPANY_CODE}		    		
    AND CONT_NO = ${CONT_NO}
    AND CONT_GL_SEQ = ${CONT_GL_SEQ}


