SELECT VENDOR_CODE AS SELLER_CODE
     ,GETVENDORNAME_ICT('000',VENDOR_CODE) AS SELLER_NAME
FROM ICOYBDAP_ICT
WHERE STATUS IN ('C','R')
AND HOUSE_CODE = ${house_code}
AND BID_NO = ${BID_NO}
AND BID_COUNT = ${BID_COUNT}
ORDER BY VENDOR_CODE


