UPDATE ICOYPRDT SET 
	 STATUS = 'D'
WHERE 1=1
AND HOUSE_CODE = ${house_code}
AND PR_NO = ${BD_PR_NO}


UPDATE ICOYPRDT SET 
	 BD_PR_NO = NULL
WHERE 1=1
AND HOUSE_CODE = ${house_code}
AND BD_PR_NO = ${BD_PR_NO}


UPDATE ICOYPRDT SET 
	 BD_PR_NO = ${NEW_PR_NO}
WHERE 1=1
AND HOUSE_CODE = ${house_code}
AND PR_NO || PR_SEQ = ${ PR_NO_SEQ }


