		, CHANGE_DATE			= TO_CHAR(SYSDATE,'YYYYMMDD')
		, CHANGE_TIME			= TO_CHAR(SYSDATE,'HH24MISS')
		, CHANGE_USER_ID		= ${ID}
		, CHANGE_USER_NAME_LOC	= ${NAME_LOC}
		, CHANGE_USER_NAME_ENG	= ${NAME_ENG}
