INSERT INTO ICOYBDPG_ICT
						(
						  HOUSE_CODE
						, BID_NO
						, BID_COUNT
						, VOTE_COUNT
						, STATUS
						, ADD_DATE
						, ADD_TIME
						, ADD_USER_ID
						, ADD_USER_NAME_LOC
						, ADD_USER_NAME_ENG
						, ADD_USER_DEPT
						, BID_BEGIN_DATE
						, BID_BEGIN_TIME
						, BID_END_DATE
						, BID_END_TIME
						, BID_PLACE
						, BID_ETC
						, OPEN_DATE
						, OPEN_TIME
						)

SELECT
						  ${HOUSE_CODE}
						, ${BID_NO    }
						, ${BID_COUNT    }
						, ${VOTE_COUNT } +1
						, 'C'
						, TO_CHAR(SYSDATE,'YYYYMMDD')
						, TO_CHAR(SYSDATE,'HH24MISS')
						, ${ID             }
						, ${NAME_LOC       }
						, ${NAME_ENG       }
						, ${DEPT           }
						, ${BID_BEGIN_DATE }
						, ${BID_BEGIN_TIME }
						, ${BID_END_DATE   }
						, ${BID_END_TIME   }
						, BID_PLACE
						, BID_ETC
						, ${BID_END_DATE    }
						, ${BID_END_TIME    }
FROM  ICOYBDPG_ICT
WHERE HOUSE_CODE = ${HOUSE_CODE}
AND   BID_NO     = ${BID_NO    }
AND   BID_COUNT  = ${BID_COUNT }
AND   VOTE_COUNT = ${VOTE_COUNT}


         INSERT INTO ICOYBDPG_LOG_ICT
