WHERE	HOUSE_CODE 	= ${HOUSE_CODE}
  AND   BID_NO 		= ${BID_NO}
  AND   BID_COUNT 	= ${BID_COUNT}
  AND	VOTE_COUNT 	= ${VOTE_COUNT}
  AND	VENDOR_CODE = ${VENDOR_CODE}

UPDATE 	ICOYBDHD_ICT
SET		BID_STATUS = 'AC' 
WHERE   BID_NO     = ${BID_NO}
AND     BID_COUNT  = ${BID_COUNT}

SELECT NVL(MAX(VOTE_COUNT),0) VOTE_COUNT 
  FROM ICOYBDVO_ICT
 WHERE HOUSE_CODE = ${HOUSE_CODE}
   AND BID_NO = ${BID_NO}
   AND BID_COUNT = ${BID_COUNT}
   AND BID_STATUS = 'SB'
   AND STATUS IN ('C','R')
   AND BID_CANCEL_FLAG = 'N'
