service.method:

			UPDATE ICOMVNIT_ICT
			   SET TITLE           	= ${title}
			     , DESC1            = ${desc1}
			     , DESC2            = ${desc2}
			     , DATE1            = ${date1}
			     , DATE2            = ${date2}
			     , REMARK           = ${remark}
			     , STATUS           = 'R'
			     , ADD_DATE         = '#add_date#'
			     , ADD_TIME         = '#add_time#'
			     , ADD_USER_ID      = '#user_id#'
			     , CHANGE_DATE      = '#add_date#'
			     , CHANGE_TIME      = '#add_time#'
			     , CHANGE_USER_ID   = '#user_id#'
    		 WHERE HOUSE_CODE   = ${house_code}
    		   AND VENDOR_CODE  = ${vendor_code}
    		   AND SEQ          = ${seq}
               AND INFO_TYPE    = 'QR'
