service.method:

SELECT  NVL(MAX(CT2.CONFIRM_FLAG),'N') CONFIRM_FLAG 
FROM 	ICOYBDCT2_ICT CT2	
WHERE 	CT2.HOUSE_CODE = ${HOUSE_CODE}
AND 	CT2.BID_NO      = ${BID_NO}
AND 	CT2.BID_COUNT   = ${BID_COUNT}

DELETE 	FROM ICOYBDCT2_ICT
WHERE  	HOUSE_CODE = ${HOUSE_CODE}
AND 	BID_NO = ${BID_NO}
AND		BID_COUNT = ${BID_COUNT}
AND		VENDOR_CODE = ${COMPANY_CODE}

INSERT INTO ICOYBDCT2_ICT(
	HOUSE_CODE
,	BID_NO
,	BID_COUNT
,	VENDOR_CODE
,	SUBMIT_DATE
,	SUBMIT_TIME
,	SUBMIT_USER_ID
,	ATTACH_NO
)VALUES(
	${HOUSE_CODE}
,	${BID_NO}
,	${BID_COUNT}
,	${COMPANY_CODE}
,	TO_CHAR(SYSDATE,'YYYYMMDD')
,	TO_CHAR(SYSDATE,'HH24MISS')
,	${ID}
,	${ATTACH_NO}
)
