    		 , CUR			    = ${CUR}
    		 , PROJECT_AMT	    = ${PROJECT_AMT}
    		 , PROJECT_TYPE	 	= ${PROJECT_TYPE}
    		 , REMARK	        = ${REMARK}
    		 , STATUS           = 'R'
    		 , ADD_DATE         = '#add_date#'
    		 , ADD_TIME         = '#add_time#'
    		 , ADD_USER_ID      = '#user_id#'
    		 , CHANGE_DATE      = '#add_date#'
    		 , CHANGE_TIME      = '#add_time#'
    		 , CHANGE_USER_ID   = '#user_id#'
    		 WHERE HOUSE_CODE   = ${house_code}
    		 AND VENDOR_CODE    = ${vendor_code}
    		 AND SEQ            = ${SEQ}



	 DELETE FROM ICOMVNPJ
    		 WHERE HOUSE_CODE = ${house_code}
    		 AND VENDOR_CODE  = ${vendor_code}
    		 AND SEQ          = ${SEQ}



