service.method:
select use_flag, code, text2, text1, text3, text4, text5, text6, text7 from scode where type = ${type} and house_code = ${house_code}
and code not in ('01','02','03')
and code = ${code}


DELETE FROM
	SCODE
WHERE
	TYPE       = ${TYPE}
AND
	CODE       = ${CODE}
AND
	HOUSE_CODE = ${HOUSE_CODE}


SELECT
	DECODE(COUNT(CODE), 0, 'Y', 'N') AS CNT
FROM
	SCODE
WHERE
	HOUSE_CODE = ${HOUSE_CODE}
AND
	TYPE       = ${TYPE}
AND
	CODE       = ${CODE}


DELETE FROM
	SCODE
WHERE
	TYPE = ${TYPE}
AND
	CODE = ${CODE}
AND
	TEXT3 = ${TEXT3}
AND
	HOUSE_CODE = ${HOUSE_CODE}


