service.method:

SELECT COUNT(A.ITEM_CODE) CNT
FROM ICOYTXDT A, ICOMMTGL B
WHERE A.HOUSE_CODE = B.HOUSE_CODE
AND A.ITEM_CODE = B.ITEM_NO
AND '03' = ${KTGRM}
AND B.MATERIAL_TYPE NOT IN ('02','04')
AND A.HOUSE_CODE = ${session.HOUSE_CODE}   
AND A.TAX_NO = ${TAX_NO}


UPDATE ICOYTXHD SET CHANGE_DATE = TO_CHAR(SYSDATE,'YYYYMMDD')
					,CHANGE_TIME = TO_CHAR(SYSDATE,'HH24MISS')
					,CHANGE_USER_ID = ${session.ID}  
					,KTGRM =  ${KTGRM}
				    ,PROC_DEPT = $S{PROC_DEPT}
		WHERE HOUSE_CODE = ${session.HOUSE_CODE}   
  			  AND TAX_NO = ${TAX_NO}


UPDATE ICOYTXHD SET CHANGE_DATE = TO_CHAR(SYSDATE,'YYYYMMDD'),
					CHANGE_TIME = TO_CHAR(SYSDATE,'HH24MISS'),
					CHANGE_USER_ID = ${session.ID},  
					PROGRESS_CODE =  ${approval_code}	

					,PAY_DEMAND_DATE = TO_CHAR(SYSDATE,'YYYYMMDD')
					,PAY_DEMAND_FLAG = 'Y'

		WHERE HOUSE_CODE = ${session.HOUSE_CODE}   
  			  AND TAX_NO = ${TAX_NO}

/* 세금계산서 헤더 */ SELECT DISTINCT TXHD.TAX_NO, /* 세금계산서 번호 */ TO_CHAR(TO_DATE(TXHD.TAX_DATE),'YYYY-MM-DD') AS TAX_DATE , /* 발행일자 */ TXHD.TAX_APP_NO, /* 승인번호 */ TXHD.SUP_IRS_NO, /* 공급자 사업자등록번호 */ TXHD.SUP_COM_CODE, /* 공급자 회사코드 */ TXHD.SUP_COM_NAME, /* 공급자 회사명 */ /* GETVENDORNAME(TXHD.HOUSE_CODE,TXHD.SUP_COM_CODE) AS SUP_COM_NAME, 공급자 회사명 */ TXHD.SUP_CEO_USER_NAME, /* 공급자 대표자 */ TXHD.SUP_BUSINESS_TYPE, /* 공급자 업태 */ TXHD.SUP_INDUSTRY_TYPE, /* 공급자 업종 */ TXHD.SUP_ADDRESS, /* 공급자 주소 */ '' AS SUP_USER_ID, /* 공급자 담당자 */ TRHD.S_PHONE_NO, /* 공급자 연락처 */ TRHD.S_EMAIL, /* 공급자 E-MAIL */ TXHD.BUYER_IRS_NO, /* 공급받는자 사업자등록번호 */ TXHD.BUYER_COM_NAME, /* 공급받는자 회사명 */ TXHD.BUYER_CEO_USER_NAME, /* 공급받는자 대표자 */ TXHD.BUYER_BUSINESS_TYPE, /* 공급받는자 업태 */ TXHD.BUYER_INDUSTRY_TYPE, /* 공급받는자 업종 */ TXHD.BUYER_ADDRESS, /* 공급받는자 주소 */ '' AS BUYER_USER_ID, /* 공급받는자 담당자 */ TRHD.B_PHONE_NO, /* 공급받는자 연락처 */ TRHD.B_EMAIL, /* 공급받는자 E-MAIL */ TO_CHAR(TO_DATE(TXHD.ADD_DATE),'YYYY-MM-DD') AS ADD_DATE, /* 작성일자 */ TRHD.TAX_DIV, /* 과세구분 */ GETICOMCODE1(TRHD.HOUSE_CODE,'M400',TRHD.TAX_DIV) AS TAX_DIV_TXT, /* 과세구분 */ '' AS REMARK, /* 비고 */ TXHD.SUP_AMT, /* 공급가액 */ DECODE(TRHD.TAX_DIV,'1',TRUNC(SUM(TRDT.TAX_AMT)),0) AS TAX_AMT, /* 세액 */ DECODE(TRHD.TAX_DIV,'1',(TXHD.SUP_AMT + TRUNC(SUM(TRDT.TAX_AMT))),TXHD.SUP_AMT) AS TOT_AMT, /* 합계금액 */ TXHD.ATTACH_NO, /* 첨부파일 */ (SELECT USER_NAME_LOC FROM ICOMLUSR WHERE HOUSE_CODE = TXHD.HOUSE_CODE AND USER_ID = TXHD.ADD_USER_ID) AS ADD_USER_NAME
FROM ICOYTXHD TXHD, ICOYTRHD TRHD, ICOYTRDT TRDT WHERE TXHD.HOUSE_CODE = TRHD.HOUSE_CODE AND TRHD.HOUSE_CODE = TRDT.HOUSE_CODE AND TRHD.PAY_NO = TRDT.PAY_NO AND TXHD.TAX_NO = TRDT.TAX_NO AND TXHD.HOUSE_CODE = '#house_code#' AND TXHD.TAX_NO = '#tax_no#'
GROUP BY TXHD.HOUSE_CODE, TXHD.TAX_NO, /* 세금계산서 번호 */ TXHD.TAX_DATE, /* 발행일자 */ TXHD.SUP_IRS_NO, /* 공급자 사업자등록번호 */ TXHD.TAX_APP_NO, /* 승인번호 */ TXHD.SUP_COM_CODE, /* 공급자 회사코드 */ TXHD.SUP_COM_NAME, /* 공급자 회사명 */ TXHD.SUP_CEO_USER_NAME, /* 공급자 대표자 */ TXHD.SUP_BUSINESS_TYPE, /* 공급자 업태 */ TXHD.SUP_INDUSTRY_TYPE, /* 공급자 업종 */ TXHD.SUP_ADDRESS, /* 공급자 주소 */ TRHD.S_PHONE_NO, /* 공급자 연락처 */ TRHD.S_EMAIL, /* 공급자 E-MAIL */ TXHD.BUYER_IRS_NO, /* 공급받는자 사업자등록번호 */ TXHD.BUYER_COM_NAME, /* 공급받는자 회사명 */ TXHD.BUYER_CEO_USER_NAME, /* 공급받는자 대표자 */ TXHD.BUYER_BUSINESS_TYPE, /* 공급받는자 업태 */ TXHD.BUYER_INDUSTRY_TYPE, /* 공급받는자 업종 */ TXHD.BUYER_ADDRESS, /* 공급받는자 주소 */ TRHD.B_PHONE_NO, /* 공급받는자 연락처 */ TRHD.B_EMAIL, /* 공급받는자 E-MAIL */ TXHD.ADD_DATE, /* 작성일자 */ TRHD.HOUSE_CODE, TRHD.TAX_DIV, /* 과세구분 */ TXHD.SUP_AMT, /* 공급가액 */ TXHD.ATTACH_NO, /* 첨부파일 */ TXHD.ADD_USER_ID
